import React, { useRef } from 'react';
import { Modal, Form, Button } from 'react-bootstrap';
import './NewContactModal.css'; // Make sure to create this CSS file

export default function NewContactModal({ closeModal }) {
  const idRef = useRef();
  const nameRef = useRef();

  function handleSubmit(e) {
    e.preventDefault();
    // createContact(idRef.current.value, nameRef.current.value);
    closeModal();
  }

  return (
    <Modal className="custom-modal" show onHide={closeModal}>
      <Modal.Header closeButton>
        <Modal.Title>Create Contact</Modal.Title>
      </Modal.Header>
      <Modal.Body>
        <Form onSubmit={handleSubmit}>
          <Form.Group>
            <Form.Label>Id</Form.Label>
            <Form.Control type="text" ref={idRef} required className="custom-input" />
          </Form.Group>
          <Form.Group>
            <Form.Label>Name</Form.Label>
            <Form.Control type="text" ref={nameRef} required className="custom-input" />
          </Form.Group>
          <Button type="submit" className="custom-button">Create</Button>
        </Form>
      </Modal.Body>
    </Modal>
  );
}
