import React, { useState } from 'react';
import { Tab, Nav, Button, Modal } from 'react-bootstrap';
import Conversations from './Conversations';
import Contacts from './Contacts';
import NewContactModal from './NewContactModal';
import NewConversationModal from './NewConversationModal';

const CONVERSATIONS_KEY = "conversations";
const CONTACTS_KEY = "contacts";

export default function Sidebar({ id }) {
  const [activeKey, setActiveKey] = useState(CONVERSATIONS_KEY);
  const [modalOpen, setModalOpen] = useState(false);
  const conversationOpen = activeKey === CONVERSATIONS_KEY;

  function closeModal() {
    setModalOpen(false);
  }

  return (
    // `div` الحاوي كإطار حول كامل الشاشة
    <div style={{
      width: '100vw',
      height: '100vh',
      border: '5px solid #007bff', // تحديد لون وسمك الحد
      boxSizing: 'border-box', // لمنع تأثير الحد على الأبعاد
      display: 'flex',
      alignItems: 'flex-start', // محاذاة المحتوى إلى الأعلى
      justifyContent: 'flex-start' // محاذاة المحتوى إلى اليسار
    }}>
      {/* السايدبار */}
      <div style={{ width: '250px' }} className='d-flex flex-column'>
        <Tab.Container activeKey={activeKey} onSelect={setActiveKey}>
          <Nav variant="tabs" className="justify-content-center">
            <Nav.Item>
              <Nav.Link eventKey={CONVERSATIONS_KEY}>
                Conversations
              </Nav.Link>
            </Nav.Item>
            <Nav.Item>
              <Nav.Link eventKey={CONTACTS_KEY}>
                Contacts
              </Nav.Link>
            </Nav.Item>
          </Nav>
          <Tab.Content className="border-right overflow-auto flex-grow-1">
            <Tab.Pane eventKey={CONVERSATIONS_KEY}>
              <Conversations/>
            </Tab.Pane>
            <Tab.Pane eventKey={CONTACTS_KEY}>
              <Contacts/>
            </Tab.Pane>
          </Tab.Content>
          <div className="p-2 border-top border-right small">
            Your Id: <span className='text-muted'>{id}</span>
          </div>
          <Button className="rounded-0" onClick={() => setModalOpen(true)}>
            NEW {conversationOpen ? 'conversation' : 'Contact'}
          </Button>
        </Tab.Container>
        <Modal show={modalOpen} onHide={closeModal}>
          {conversationOpen ?
            <NewConversationModal closeModal={closeModal}/> : 
            <NewContactModal closeModal={closeModal}/>
          }
        </Modal>
      </div>
    </div>
  );
}
