import React, { useState, useRef } from 'react';
import { Modal, Form, Button } from 'react-bootstrap';

function NewContactModal({ closeModal }) {
  const idRef = useRef();
  const nameRef = useRef();

  async function handleSubmit(e) {
    e.preventDefault();
    console.log("Form submitted");

    try {
      // Uncomment and implement your createContact function
      // await createContact(idRef.current.value, nameRef.current.value);

      console.log("Contact created with ID:", idRef.current.value, "and Name:", nameRef.current.value);
      closeModal(); // Close modal after successful submission
    } catch (error) {
      console.error("Error creating contact:", error);
    }
  }

  return (
    <Modal onHide={closeModal}>
      <Modal.Header closeButton>
        <Modal.Title>Create Contact</Modal.Title>
      </Modal.Header>
      <Modal.Body>
        <Form onSubmit={handleSubmit}>
          <Form.Group>
            <Form.Label>Id</Form.Label>
            <Form.Control type="text" ref={idRef} required />
          </Form.Group>
          <Form.Group>
            <Form.Label>Name</Form.Label>
            <Form.Control type="text" ref={nameRef} required />
          </Form.Group>
          <Button type="submit">Create</Button>
        </Form>
      </Modal.Body>
      <Modal.Footer>
        <Button variant="secondary" onClick={closeModal}>
          Close
        </Button>
      </Modal.Footer>
    </Modal>
  );
}

export default function App() {
  const [showModal, setShowModal] = useState(false);

  const handleOpen = () => setShowModal(true);
  const handleClose = () => setShowModal(false);

  return (
    <>
      <Button variant="primary" onClick={handleOpen}>
        Add Contact
      </Button>

      {showModal && (
        <NewContactModal closeModal={handleClose} />
      )}
    </>
  );
}
